#Creates a dict {name : detected} from vars.apivoid_engines list of dicts#
{%set undetected={}%}
{%set detected={}%}
{%if vars.apivoid_engines|length >0%}
{%for k,v in vars.apivoid_engines.items()%}
{%if v.detected%}
{{detected.update({v.engine:vars.format_red.format('Detected')})}}
{%else%}
{{undetected.update({v.engine:vars.format_green.format('Undetected')})}}
{%endif%}
{%endfor%}
{%endif%}
{%set tmp_json=detected|combine(undetected)%}
{%if tmp_json%}
{{vars.list_of_lists.append(['Detected by Engines',tmp_json])}}
{%endif%}
